mapper.select:
SELECT ifnull(count(*), 0)
FROM comment
WHERE board_seq=#{seq}

mapper.update:
UPDATE comment
SET del = 1
WHERE seq = #{seq}

mapper.select:
SELECT seq, email, content, board_seq boardSeq, write_date writeDate, del
FROM comment
WHERE seq=#{seq}

mapper.update:
UPDATE comment
SET content = #{content}
WHERE seq = #{seq}
